e)\n            }\n            _this.pre=function (x) {\n                f.eq(b.index).removeClass(b.className); //移除导航的类\n                b.index--; //下一个\n                if(b.index&lt;0) {//如果是最后一个\n                    b.index=f.length-1\n                }\n                c()\n            }\n        }\n\n        if(b.stop) {\n            //对外接口\n            _this.setStop=function () {\n                clearTimeout(b.subTimer);\n                clearInterval(b.mainTimer);\n            }\n            _this.setStart=function () {\n                clearTimeout(b.subTimer);\n                clearInterval(b.mainTimer);\n                if(b.auto) {\n                    _auto()\n                }\n            }\n        }\n\n        function to(m,l) {\n            b.subTimer=setTimeout(function () {\n                f.eq(b.index).removeClass(b.className); //移除导航的类\n                if(l) {\n                    b.index++; //下一个\n                    if(b.index==f.length) {//如果是最后一个\n                        b.index=0\n                    }\n                } else {\n                    b.index=m\n                }\n                c()\n            },\n            b.delay)\n        }\n\n        var c=function () {\n            f.eq(b.index).addClass(b.className); //高亮导航\n            if(b.show) {\n                e.stop().hide();\n                b.fadeIn?e.eq(b.index).fadeIn(500):e.eq(b.index).show();\n            }\n            if(h) {\n                h(f.eq(b.index),e.eq(b.index),b.index); //返回值 { 导航当前对象, 内容当前对象, 索引}\n            }\n        };\n\n        var _auto=function () {\n            b.mainTimer=setInterval(function () {\n                to(b.index,true)\n            },\n            b.speed)\n        };\n\n        f.each(function (i,n) {\n            var _this=a(n);\n            _this.bind(b.event,function () {\n                if(_this.hasClass(b.className)) {//如果操作的为当前类则\n                    return\n                }\n                clearTimeout(b.subTimer);\n                clearInterval(b.mainTimer);\n                to(i,false)\n            })\n            .bind(&quot;mouseleave&quot;,function () {\n                clearTimeout(b.subTimer);\n                clearInterval(b.mainTimer);\n                if(b.auto) {\n                    _auto()\n                }\n            });\n        });\n        if(b.auto) {//自动\n            _auto()\n        }\n        //更新index为当前高亮的,解决index如果不是0的时候bug;\n        var _i = f.filter(function () {\n            return $(this).hasClass(b.className);\n        }).index();\n        b.index = _i === -1 ? 0: _i;\n\n        if(b.run) {//如果直接运行\n            f.removeClass(b.className).eq(0).trigger(b.event);\n            if(b.auto) {//自动\n                _auto()\n            }\n        }\n        return _this;//返回this让jQuery对象支持连写\n    }\n})(jQuery);\n</code></pre>\n<h2 id=\"jquery-\">jQuery插件优势</h2>\n<ul>\n<li>它精简,压缩后可达到821B(Gzip);</li>\n<li>它功能强大,可完成 幻灯,tab标签等;</li>\n<li>接口全,可自定义标签的名称,键值,使jQuery选择器查找的更快(推荐阅读下:你是否在滥用jQuery),可自动,可设延迟,循环时间,可自定义事件等,让你无所不能;</li>\n<li>对外开放接口,如 开始,停止,上一个,下一个等;</li>\n<li>可自动运行,{run:true},解决程序循环必须让第一个窗口显示的问题;</li>\n<li>基于jQuery.fn扩展,支持连写,可在页面多次使用,互不冲突;</li>\n<li>配有回调代码,可完美扩展出按需加载,翻转等效果,循环播放,自动播放,上一个,下一个等效果;</li>\n<li>可根据自已的需求更改/扩展代码;</li>\n<li>....更多有待你的发现;</li>\n</ul>\n<p>Demo地址: <a href=\"http://www.xuexb.com/demo/tab/\">jQuery幻灯</a></p>\n','工作中由于大量使用幻灯,标签页,选项卡等效果,项目是基于jQuery开发的,于是乎写了个插件(当然多谢jd的大力引导),感觉挺好用,实用的,就分享给大家吧.代码如下:\r\n\r\n```js\r\n/*\r\n\r\n#   author xieliang;\r\n#   基于jQuery开发;\r\n#   www.xuexb.com\r\n#	建议使用jQuery 1.8.3\r\n\r\n*/\r\n\r\n;(function (a) {\r\n    a.fn.tab=function (d,h) {\r\n        /// <summary>\r\n        /// Tab选项卡\r\n        /// </summary>\r\n        /// <param name=\"d\">obj or function 返回(导航当前对象, 内容当前对象, 索引)</param>\r\n		var _this = this;\r\n        if(!_this.length) {\r\n            return _this\r\n        }\r\n        if(typeof d===\"function\") {\r\n            h=d;\r\n            d={}\r\n        };\r\n        var b=a.extend({\r\n  